import com.psabackoffice.job1111.Sasnotes;


/**
 * ServiceImpl object for domain model class Sasnotes.
 *
 * @see Sasnotes
 */
@Service("Job1111.SasnotesService")
@Validated
public class SasnotesServiceImpl implements SasnotesService {

    private static final Logger LOGGER = LoggerFactory.getLogger(SasnotesServiceImpl.class);


    @Autowired
    @Qualifier("Job1111.SasnotesDao")
    private WMGenericDao<Sasnotes, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Sasnotes, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "Job1111TransactionManager")
    @Override
	public Sasnotes create(Sasnotes sasnotes) {
        LOGGER.debug("Creating a new Sasnotes with information: {}", sasnotes);

        Sasnotes sasnotesCreated = this.wmGenericDao.create(sasnotes);
        // reloading object from database to get database defined & server defined values.
        return this.wmGenericDao.refresh(sasnotesCreated);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public Sasnotes getById(Integer sasnotesId) {
        LOGGER.debug("Finding Sasnotes by id: {}", sasnotesId);
        return this.wmGenericDao.findById(sasnotesId);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public Sasnotes findById(Integer sasnotesId) {
        LOGGER.debug("Finding Sasnotes by id: {}", sasnotesId);
        try {
            return this.wmGenericDao.findById(sasnotesId);
        } catch(EntityNotFoundException ex) {
            LOGGER.debug("No Sasnotes found with id: {}", sasnotesId, ex);
            return null;
        }
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "Job1111TransactionManager")
	@Override
	public Sasnotes update(Sasnotes sasnotes) {
        LOGGER.debug("Updating Sasnotes with information: {}", sasnotes);

        this.wmGenericDao.update(sasnotes);
        this.wmGenericDao.refresh(sasnotes);

        return sasnotes;
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public Sasnotes delete(Integer sasnotesId) {
        LOGGER.debug("Deleting Sasnotes with id: {}", sasnotesId);
        Sasnotes deleted = this.wmGenericDao.findById(sasnotesId);
        if (deleted == null) {
            LOGGER.debug("No Sasnotes found with id: {}", sasnotesId);
            throw new EntityNotFoundException(String.valueOf(sasnotesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public void delete(Sasnotes sasnotes) {
        LOGGER.debug("Deleting Sasnotes with {}", sasnotes);
        this.wmGenericDao.delete(sasnotes);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public Page<Sasnotes> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Sasnotes");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Page<Sasnotes> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Sasnotes");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service Job1111 for table Sasnotes to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

