import com.psabackoffice.job1111.SteelMisc;


/**
 * ServiceImpl object for domain model class SteelMisc.
 *
 * @see SteelMisc
 */
@Service("Job1111.SteelMiscService")
@Validated
public class SteelMiscServiceImpl implements SteelMiscService {

    private static final Logger LOGGER = LoggerFactory.getLogger(SteelMiscServiceImpl.class);


    @Autowired
    @Qualifier("Job1111.SteelMiscDao")
    private WMGenericDao<SteelMisc, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<SteelMisc, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "Job1111TransactionManager")
    @Override
	public SteelMisc create(SteelMisc steelMisc) {
        LOGGER.debug("Creating a new SteelMisc with information: {}", steelMisc);

        SteelMisc steelMiscCreated = this.wmGenericDao.create(steelMisc);
        // reloading object from database to get database defined & server defined values.
        return this.wmGenericDao.refresh(steelMiscCreated);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelMisc getById(Integer steelmiscId) {
        LOGGER.debug("Finding SteelMisc by id: {}", steelmiscId);
        return this.wmGenericDao.findById(steelmiscId);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelMisc findById(Integer steelmiscId) {
        LOGGER.debug("Finding SteelMisc by id: {}", steelmiscId);
        try {
            return this.wmGenericDao.findById(steelmiscId);
        } catch(EntityNotFoundException ex) {
            LOGGER.debug("No SteelMisc found with id: {}", steelmiscId, ex);
            return null;
        }
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "Job1111TransactionManager")
	@Override
	public SteelMisc update(SteelMisc steelMisc) {
        LOGGER.debug("Updating SteelMisc with information: {}", steelMisc);

        this.wmGenericDao.update(steelMisc);
        this.wmGenericDao.refresh(steelMisc);

        return steelMisc;
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public SteelMisc delete(Integer steelmiscId) {
        LOGGER.debug("Deleting SteelMisc with id: {}", steelmiscId);
        SteelMisc deleted = this.wmGenericDao.findById(steelmiscId);
        if (deleted == null) {
            LOGGER.debug("No SteelMisc found with id: {}", steelmiscId);
            throw new EntityNotFoundException(String.valueOf(steelmiscId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public void delete(SteelMisc steelMisc) {
        LOGGER.debug("Deleting SteelMisc with {}", steelMisc);
        this.wmGenericDao.delete(steelMisc);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public Page<SteelMisc> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all SteelMiscs");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Page<SteelMisc> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all SteelMiscs");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service Job1111 for table SteelMisc to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

