package com.psabackoffice.job1111.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/

import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.data.model.AggregationInfo;
import com.wavemaker.runtime.file.model.Downloadable;

import com.psabackoffice.job1111.SteelSell;
import com.psabackoffice.job1111.SteelSellRev;


/**
 * ServiceImpl object for domain model class SteelSell.
 *
 * @see SteelSell
 */
@Service("Job1111.SteelSellService")
public class SteelSellServiceImpl implements SteelSellService {

    private static final Logger LOGGER = LoggerFactory.getLogger(SteelSellServiceImpl.class);

    @Autowired
	@Qualifier("Job1111.SteelSellRevService")
	private SteelSellRevService steelSellRevService;

    @Autowired
    @Qualifier("Job1111.SteelSellDao")
    private WMGenericDao<SteelSell, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<SteelSell, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "Job1111TransactionManager")
    @Override
	public SteelSell create(SteelSell steelSell) {
        LOGGER.debug("Creating a new SteelSell with information: {}", steelSell);
        SteelSell steelSellCreated = this.wmGenericDao.create(steelSell);
        if(steelSellCreated.getSteelSellRevs() != null) {
            for(SteelSellRev steelSellRev : steelSellCreated.getSteelSellRevs()) {
                steelSellRev.setSteelSell(steelSellCreated);
                LOGGER.debug("Creating a new child SteelSellRev with information: {}", steelSellRev);
                steelSellRevService.create(steelSellRev);
            }
        }
        return steelSellCreated;
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelSell getById(Integer steelsellId) throws EntityNotFoundException {
        LOGGER.debug("Finding SteelSell by id: {}", steelsellId);
        SteelSell steelSell = this.wmGenericDao.findById(steelsellId);
        if (steelSell == null){
            LOGGER.debug("No SteelSell found with id: {}", steelsellId);
            throw new EntityNotFoundException(String.valueOf(steelsellId));
        }
        return steelSell;
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelSell findById(Integer steelsellId) {
        LOGGER.debug("Finding SteelSell by id: {}", steelsellId);
        return this.wmGenericDao.findById(steelsellId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "Job1111TransactionManager")
	@Override
	public SteelSell update(SteelSell steelSell) throws EntityNotFoundException {
        LOGGER.debug("Updating SteelSell with information: {}", steelSell);
        this.wmGenericDao.update(steelSell);

        Integer steelsellId = steelSell.getId();

        return this.wmGenericDao.findById(steelsellId);
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public SteelSell delete(Integer steelsellId) throws EntityNotFoundException {
        LOGGER.debug("Deleting SteelSell with id: {}", steelsellId);
        SteelSell deleted = this.wmGenericDao.findById(steelsellId);
        if (deleted == null) {
            LOGGER.debug("No SteelSell found with id: {}", steelsellId);
            throw new EntityNotFoundException(String.valueOf(steelsellId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public Page<SteelSell> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all SteelSells");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Page<SteelSell> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all SteelSells");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service Job1111 for table SteelSell to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
    @Override
    public Page<SteelSellRev> findAssociatedSteelSellRevs(Integer id, Pageable pageable) {
        LOGGER.debug("Fetching all associated steelSellRevs");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("steelSell.id = '" + id + "'");

        return steelSellRevService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service SteelSellRevService instance
	 */
	protected void setSteelSellRevService(SteelSellRevService service) {
        this.steelSellRevService = service;
    }

}

