    @Transactional(value = "Job1111TransactionManager")
    @Override
	public SteelWeld create(SteelWeld steelWeld) {
        LOGGER.debug("Creating a new SteelWeld with information: {}", steelWeld);
        SteelWeld steelWeldCreated = this.wmGenericDao.create(steelWeld);
        return steelWeldCreated;
    }

	@Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelWeld getById(Integer steelweldId) throws EntityNotFoundException {
        LOGGER.debug("Finding SteelWeld by id: {}", steelweldId);
        SteelWeld steelWeld = this.wmGenericDao.findById(steelweldId);
        if (steelWeld == null){
            LOGGER.debug("No SteelWeld found with id: {}", steelweldId);
            throw new EntityNotFoundException(String.valueOf(steelweldId));
        }
        return steelWeld;
    }

    @Transactional(readOnly = true, value = "Job1111TransactionManager")
	@Override
	public SteelWeld findById(Integer steelweldId) {
        LOGGER.debug("Finding SteelWeld by id: {}", steelweldId);
        return this.wmGenericDao.findById(steelweldId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "Job1111TransactionManager")
	@Override
	public SteelWeld update(SteelWeld steelWeld) throws EntityNotFoundException {
        LOGGER.debug("Updating SteelWeld with information: {}", steelWeld);
        this.wmGenericDao.update(steelWeld);

        Integer steelweldId = steelWeld.getActivityId();

        return this.wmGenericDao.findById(steelweldId);
    }

    @Transactional(value = "Job1111TransactionManager")
	@Override
	public SteelWeld delete(Integer steelweldId) throws EntityNotFoundException {
        LOGGER.debug("Deleting SteelWeld with id: {}", steelweldId);
        SteelWeld deleted = this.wmGenericDao.findById(steelweldId);
        if (deleted == null) {
            LOGGER.debug("No SteelWeld found with id: {}", steelweldId);
            throw new EntityNotFoundException(String.valueOf(steelweldId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

