package com.psabackoffice.psa.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/

import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;
import org.springframework.validation.annotation.Validated;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.data.model.AggregationInfo;
import com.wavemaker.runtime.file.model.Downloadable;

import com.psabackoffice.psa.ChatMessages;


/**
 * ServiceImpl object for domain model class ChatMessages.
 *
 * @see ChatMessages
 */
@Service("PSA.ChatMessagesService")
@Validated
public class ChatMessagesServiceImpl implements ChatMessagesService {

    private static final Logger LOGGER = LoggerFactory.getLogger(ChatMessagesServiceImpl.class);


    @Autowired
    @Qualifier("PSA.ChatMessagesDao")
    private WMGenericDao<ChatMessages, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<ChatMessages, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "PSATransactionManager")
    @Override
	public ChatMessages create(ChatMessages chatMessages) {
        LOGGER.debug("Creating a new ChatMessages with information: {}", chatMessages);

        ChatMessages chatMessagesCreated = this.wmGenericDao.create(chatMessages);
        // reloading object from database to get database defined & server defined values.
        return this.wmGenericDao.refresh(chatMessagesCreated);
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public ChatMessages getById(Integer chatmessagesId) {
        LOGGER.debug("Finding ChatMessages by id: {}", chatmessagesId);
        return this.wmGenericDao.findById(chatmessagesId);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public ChatMessages findById(Integer chatmessagesId) {
        LOGGER.debug("Finding ChatMessages by id: {}", chatmessagesId);
        try {
            return this.wmGenericDao.findById(chatmessagesId);
        } catch(EntityNotFoundException ex) {
            LOGGER.debug("No ChatMessages found with id: {}", chatmessagesId, ex);
            return null;
        }
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "PSATransactionManager")
	@Override
	public ChatMessages update(ChatMessages chatMessages) {
        LOGGER.debug("Updating ChatMessages with information: {}", chatMessages);

        this.wmGenericDao.update(chatMessages);
        this.wmGenericDao.refresh(chatMessages);

        return chatMessages;
    }

    @Transactional(value = "PSATransactionManager")
	@Override
	public ChatMessages delete(Integer chatmessagesId) {
        LOGGER.debug("Deleting ChatMessages with id: {}", chatmessagesId);
        ChatMessages deleted = this.wmGenericDao.findById(chatmessagesId);
        if (deleted == null) {
            LOGGER.debug("No ChatMessages found with id: {}", chatmessagesId);
            throw new EntityNotFoundException(String.valueOf(chatmessagesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

    @Transactional(value = "PSATransactionManager")
	@Override
	public void delete(ChatMessages chatMessages) {
        LOGGER.debug("Deleting ChatMessages with {}", chatMessages);
        this.wmGenericDao.delete(chatMessages);
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public Page<ChatMessages> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all ChatMessages");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Page<ChatMessages> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all ChatMessages");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service PSA for table ChatMessages to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

