/**
 * ServiceImpl object for domain model class RefDisciplines.
 *
 * @see RefDisciplines
 */
@Service("PSA.RefDisciplinesService")
public class RefDisciplinesServiceImpl implements RefDisciplinesService {

    private static final Logger LOGGER = LoggerFactory.getLogger(RefDisciplinesServiceImpl.class);

    @Autowired
	@Qualifier("PSA.TblCrewsService")
	private TblCrewsService tblCrewsService;

    @Autowired
	@Qualifier("PSA.TblUserPsaService")
	private TblUserPsaService tblUserPsaService;

    @Autowired
    @Qualifier("PSA.RefDisciplinesDao")
    private WMGenericDao<RefDisciplines, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<RefDisciplines, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "PSATransactionManager")
    @Override
	public RefDisciplines create(RefDisciplines refDisciplines) {
        LOGGER.debug("Creating a new RefDisciplines with information: {}", refDisciplines);
        RefDisciplines refDisciplinesCreated = this.wmGenericDao.create(refDisciplines);
        if(refDisciplinesCreated.getTblCrewses() != null) {
            for(TblCrews tblCrewse : refDisciplinesCreated.getTblCrewses()) {
                tblCrewse.setRefDisciplines(refDisciplinesCreated);
                LOGGER.debug("Creating a new child TblCrews with information: {}", tblCrewse);
                tblCrewsService.create(tblCrewse);
            }
        }

        if(refDisciplinesCreated.getTblUserPsas() != null) {
            for(TblUserPsa tblUserPsa : refDisciplinesCreated.getTblUserPsas()) {
                tblUserPsa.setRefDisciplines(refDisciplinesCreated);
                LOGGER.debug("Creating a new child TblUserPsa with information: {}", tblUserPsa);
                tblUserPsaService.create(tblUserPsa);
            }
        }
        return refDisciplinesCreated;
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public RefDisciplines getById(Integer refdisciplinesId) throws EntityNotFoundException {
        LOGGER.debug("Finding RefDisciplines by id: {}", refdisciplinesId);
        RefDisciplines refDisciplines = this.wmGenericDao.findById(refdisciplinesId);
        if (refDisciplines == null){
            LOGGER.debug("No RefDisciplines found with id: {}", refdisciplinesId);
            throw new EntityNotFoundException(String.valueOf(refdisciplinesId));
        }
        return refDisciplines;
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public RefDisciplines findById(Integer refdisciplinesId) {
        LOGGER.debug("Finding RefDisciplines by id: {}", refdisciplinesId);
        return this.wmGenericDao.findById(refdisciplinesId);
    }


	@Transactional(rollbackFor = EntityNotFoundException.class, value = "PSATransactionManager")
	@Override
	public RefDisciplines update(RefDisciplines refDisciplines) throws EntityNotFoundException {
        LOGGER.debug("Updating RefDisciplines with information: {}", refDisciplines);
        this.wmGenericDao.update(refDisciplines);

        Integer refdisciplinesId = refDisciplines.getId();

        return this.wmGenericDao.findById(refdisciplinesId);
    }

    @Transactional(value = "PSATransactionManager")
	@Override
	public RefDisciplines delete(Integer refdisciplinesId) throws EntityNotFoundException {
        LOGGER.debug("Deleting RefDisciplines with id: {}", refdisciplinesId);
        RefDisciplines deleted = this.wmGenericDao.findById(refdisciplinesId);
        if (deleted == null) {
            LOGGER.debug("No RefDisciplines found with id: {}", refdisciplinesId);
            throw new EntityNotFoundException(String.valueOf(refdisciplinesId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public Page<RefDisciplines> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all RefDisciplines");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Page<RefDisciplines> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all RefDisciplines");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service PSA for table RefDisciplines to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Page<TblCrews> findAssociatedTblCrewses(Integer id, Pageable pageable) {
        LOGGER.debug("Fetching all associated tblCrewses");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("refDisciplines.id = '" + id + "'");

        return tblCrewsService.findAll(queryBuilder.toString(), pageable);
    }

    @Transactional(readOnly = true, value = "PSATransactionManager")
    @Override
    public Page<TblUserPsa> findAssociatedTblUserPsas(Integer id, Pageable pageable) {
        LOGGER.debug("Fetching all associated tblUserPsas");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("refDisciplines.id = '" + id + "'");

        return tblUserPsaService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service TblCrewsService instance
	 */
	protected void setTblCrewsService(TblCrewsService service) {
        this.tblCrewsService = service;
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service TblUserPsaService instance
	 */
	protected void setTblUserPsaService(TblUserPsaService service) {
        this.tblUserPsaService = service;
    }

}

